{
	var tdate = null;

	var tmp = inDate.split("/");
	if ( tmp.length < 3 )
		tmp = inDate.split("-");

	if ( tmp.length == 3 )
		tdate = new Date ( parseInt(tmp[2]),
									parseInt(tmp[1])-1,
									parseInt(tmp[0]));

	return tdate;

}


